package com.syntax.class07;

public class WhileLooppractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	//print only odd numbers from 100 to 1
		
		int o=100;
		while (o>=1) {
			if (o%2!=0)
			System.out.println(o+" ");
			o--;}
		
		
		
		// from 20 to 100 even 
		int h=20;
		while (h<=100) {
			if(h%2==0) {
			System.out.println(h+" ");
			}
			h++;
			
		}
	}

}
